package com.tenXen.core.domain;public class Message extends BaseDomain {    private static final long serialVersionUID = 1L;    /**     * 发送人     **/    private Integer user;    /**     * 接收人     **/    private Integer toUser;    /**     * 接收群     **/    private Integer toGroup;    /**     * 消息类型     **/    private Integer type;    /**     * 内容     **/    private String content;    /**     * 是否已读     **/    private Integer isRead;    public Integer getUser() {        return this.user;    }    public void setUser(Integer user) {        this.user = user;    }    public Integer getToUser() {        return this.toUser;    }    public void setToUser(Integer toUser) {        this.toUser = toUser;    }    public Integer getToGroup() {        return this.toGroup;    }    public void setToGroup(Integer toGroup) {        this.toGroup = toGroup;    }    public Integer getType() {        return this.type;    }    public void setType(Integer type) {        this.type = type;    }    public String getContent() {        return this.content;    }    public void setContent(String content) {        this.content = content;    }    public Integer getIsRead() {        return this.isRead;    }    public void setIsRead(Integer isRead) {        this.isRead = isRead;    }}