package com.tenXen.core.domain;public class UserGroup extends BaseDomain {    private static final long serialVersionUID = 1L;    /**     * 用户ID     **/    private Integer userId;    /**     * 群组ID     **/    private Integer groupId;    /**     * 关系     **/    private Integer relation;    public Integer getUserId() {        return this.userId;    }    public void setUserId(Integer userId) {        this.userId = userId;    }    public Integer getGroupId() {        return this.groupId;    }    public void setGroupId(Integer groupId) {        this.groupId = groupId;    }    public Integer getRelation() {        return this.relation;    }    public void setRelation(Integer relation) {        this.relation = relation;    }}