package com.tenXen.core.domain;public class UserRelation extends BaseDomain {    private static final long serialVersionUID = 1L;    /**     * 用户ID     **/    private Integer userId;    /**     * 好友     **/    private String friends;    /**     * 群组     **/    private String groups;    public Integer getUserId() {        return this.userId;    }    public void setUserId(Integer userId) {        this.userId = userId;    }    public String getFriends() {        return this.friends;    }    public void setFriends(String friends) {        this.friends = friends;    }    public String getGroups() {        return this.groups;    }    public void setGroups(String groups) {        this.groups = groups;    }}